import axios from "axios";
import React, { useState } from "react";
import { apiPost } from "../services/requestService";

interface State {
  email: string;
  password: string;
}

// interface TokenResponse {
//   data: {
//     authorization: string;
//   };
// }

export function Login(): JSX.Element {
  const [loginInfo, setLoginInfo] = useState<State>({
    email: "",
    password: "",
  });
  async function handleSubmit(): Promise<void> {
      try {
          const x = axios.post(process.env.REACT_APP_BASE_URL!+"/api/user/login", {...loginInfo}).then(response =>console.log(response));
      }
      catch (error) {
          console.log(error)
      }
    // .then((response: any) => {
        // console.log(x.data);
    //   localStorage.setItem("authorization", response.data["authorization"]);
    // });
  }
  //   useEffect(() => {
  //     apiPost("/api/user/login", loginInfo).then(
  //       (response: any) => {
  //         localStorage.setItem('authorization', response.data["authorization"]);
  //       }
  //     );
  //   }, []);

  function handleChange(event: React.ChangeEvent<HTMLInputElement>): void {
    const value = event.target.value;
    setLoginInfo({
      ...loginInfo,
      [event.target.name]: value,
    });
  }

  return (
    <>
      <form>
        <label>
          Email:
          <input
            type="text"
            name="email"
            value={loginInfo.email}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={loginInfo.password}
            onChange={handleChange}
          />
        </label>
        <br />
        <input type="submit" onClick={handleSubmit} />
      </form>
      <a href="/registry">
        <button type="button">Sign Up</button>
      </a>
    </>
  );
}
