import React, { useEffect, useState } from "react";
import { apiGet, apiPost } from "../services/requestService";

interface State{
    email: string;
    password: string;
}

interface TokenResponse {
    data: {
        authorization: string;
    }
}

export function Login(): JSX.Element {
    const [loginInfo, setLoginInfo] = useState<State>({email: "", password: ""});
  async function handleSubmit(
    event: React.FormEvent<HTMLFormElement>
  ): Promise<void> {
    event.preventDefault();
  }
  useEffect(() => {
    apiPost("/api/user/login", loginInfo).then(
      (response: any) => {
        localStorage.setItem('authorization', response.data["authorization"]);
      }
    );
  }, []);

  function handleChange(event: React.ChangeEvent<HTMLInputElement>): void{
      const value = event.target.value;
      setLoginInfo({
          ...loginInfo,
          [event.target.name]: value
      }
      );
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input type="text" name="email" value={loginInfo.email} onChange={handleChange} />
        </label>
        <br />
        <label>
          Password:
          <input type="password" name="password" value={loginInfo.password}  onChange={handleChange}  />
        </label>
        <br />
        <input type="submit" value="Login" />
      </form>
      <a href="/registry">
        <button type="button">Sign Up</button>
      </a>
    </>
  );
}
