import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { apiGet, apiPost } from "../services/requestService";

interface NewGameResponse {
    data: {
        gameId: string;
    },
    status: number;
}

interface State {
    rows: number;
    columns: number;
    amountOfMines: number;
  }


export function GameMenu(): JSX.Element {
    const [gameInfo, setGameInfo] = useState<State>({
        rows: 0,
        columns: 0,
        amountOfMines: 0,
    });
    const history = useHistory();
    const handleNewGame = async ()=> {
        const response = await apiPost<NewGameResponse>("/api/games");
        if(response?.data?.gameId){
            history.push(`/minesweeper/${response.data.gameId}`);
        }
    }

  return (
    <div>
      <a href="/minesweeper">
        <button type="button" onClick={handleNewGame}>New Game</button>
      </a>
     <input type="number" name="rows" value={gameInfo.rows}></input>
     <input type="number" name="columns" value={gameInfo.columns}></input>
     <input type="number" name="amountOfMines" value={gameInfo.amountOfMines}></input>
    <br/>
      <a href="/saved-games">
        <button type="button">Load Game</button>
      </a>
    </div>
  );
}
