import React from "react";
import axios from "axios";

export const apiGet = async <T, R>(
  headers?: T,
  params?: R
): Promise<{ data: object }> => {
  return await axios.get(process.env.REACT_APP_BASE_URL!, {
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      "Access-Control-Allow-Origin": "*",
    },
  });
};

export const apiPost = async <T, R, Z>(
  path: string,
  body?: Z,
  headers?: T,
  params?: R
): Promise<{ data: object }> => {
  return await axios.post(process.env.REACT_APP_BASE_URL! + path, body, {
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      "Access-Control-Allow-Origin": "*",
    },
  });
};
